import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../pages/Control.css';

const Control = () => {
  const [radarData, setRadarData] = useState(null);
  const [mode, setMode] = useState('MANUAL');
  const [position, setPosition] = useState({ x: 0, y: 0, z: 0 });
  const [gripperState, setGripperState] = useState(false);

  // Î∞©Ìñ•ÌÇ§ ÏÉÅÌÉú Í¥ÄÎ¶¨ (Ï∞®Ï≤¥ Ïù¥Îèô ÏÉÅÌÉú)
  const [directionState, setDirectionState] = useState({
    up: false,
    down: false,
    left: false,
    right: false,
    moving: false, // Ï∞®Ï≤¥ Ïù¥Îèô ÏÉÅÌÉú
  });

  // Î†àÏù¥Îçî Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await axios.get('http://localhost:Ìè¨Ìä∏Î≤àÌò∏/radar');
        setRadarData(res.data.distance); // Ïà´Ïûê ÌòïÏãùÏúºÎ°ú Ï†ÄÏû•
      } catch (err) {
        setRadarData(null); // ÏùΩÍ∏∞ Ïã§Ìå® Ï≤òÎ¶¨
      }
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Î∞©Ìñ•ÌÇ§ Î∞è ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ìï®Ïàò
  const updateDirectionState = (direction, isPressed) => {
    setDirectionState((prev) => ({
      ...prev,
      [direction]: isPressed,
      moving: isPressed, // Ï∞®Ï≤¥Í∞Ä ÏõÄÏßÅÏù¥Îäî ÏÉÅÌÉúÎ°ú Í∞±Ïã†
    }));
  };

  // ÌÇ§ Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Enter ÌÇ§Î°ú ÏßëÍ≤å ÏÉÅÌÉú Î≥ÄÍ≤Ω
      if (e.key === 'Enter') {
        setGripperState((prev) => !prev); // ÏÉÅÌÉú Î∞òÏ†Ñ
      }

      // WASD QE ÌÇ§Î°ú XYZ Ï¢åÌëú ÏóÖÎç∞Ïù¥Ìä∏
      setPosition((prev) => {
        const delta = 10;
        switch (e.key.toLowerCase()) {
          case 'w':
            return { ...prev, y: prev.y + delta };
          case 's':
            return { ...prev, y: prev.y - delta };
          case 'a':
            return { ...prev, x: prev.x - delta };
          case 'd':
            return { ...prev, x: prev.x + delta };
          case 'q':
            return { ...prev, z: prev.z + delta };
          case 'e':
            return { ...prev, z: prev.z - delta };
          default:
            return prev;
        }
      });

      // IJKL ÌÇ§Î°ú Ï∞®Ï≤¥ Ïù¥Îèô ÏÉÅÌÉú Í∞±Ïã†
      if (e.key === 'i') updateDirectionState('up', true);
      if (e.key === 'k') updateDirectionState('down', true);
      if (e.key === 'j') updateDirectionState('left', true);
      if (e.key === 'l') updateDirectionState('right', true);
    };

    const handleKeyUp = (e) => {
      // IJKL ÌÇ§ ÎñºÏóàÏùÑ Îïå Ï∞®Ï≤¥ Ïù¥Îèô ÏÉÅÌÉú Î≥ÄÍ≤Ω
      if (e.key === 'i') updateDirectionState('up', false);
      if (e.key === 'k') updateDirectionState('down', false);
      if (e.key === 'j') updateDirectionState('left', false);
      if (e.key === 'l') updateDirectionState('right', false);
    };

    // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // ÌÅ¥Î¶∞ÏóÖ Ìï®Ïàò
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []); // ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥ÏùÑ Îπà Î∞∞Ïó¥Î°ú ÏÑ§Ï†ïÌïòÏó¨ Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä ÎßàÏö¥Ìä∏Îê† Îïå Ìïú Î≤àÎßå Ïã§ÌñâÎêòÎèÑÎ°ù Ìï©ÎãàÎã§.

  return (
    <div className="control-container">
      {/* ÏôºÏ™Ω: Ïπ¥Î©îÎùº / ÏÑºÏÑú */}
      <div className="left-panel">
        <div className="camera-box">
          <h2>üì∑ Ïö∞ÎÖ∏ Ïπ¥Î©îÎùº</h2>
          <img
            src="http://localhost:/Ìè¨Ìä∏Î≤àÌò∏"
            alt="Ïπ¥Î©îÎùº ÏòÅÏÉÅ"
            style={{ width: '100%', height: '100%', objectFit: 'cover' }}
          />
        </div>
        <div className="sensor-box">
          <h2>üì° Î†àÏù¥Îçî ÏÑºÏÑú</h2>
          <p className="radar-text">
            {radarData !== null ? `${radarData} cm` : 'ÏùΩÍ∏∞ Ïã§Ìå®'}
          </p>
        </div>
      </div>

      {/* Ïò§Î•∏Ï™Ω: Î™®Îìú / Î°úÎ¥á Ìåî / Ï∞®Ï≤¥ Ï†úÏñ¥ */}
      <div className="right-panel">
        <div className="mode-box">
          <h2>üéõ Î™®Îìú ÏÑ†ÌÉù</h2>
          <div className="mode-buttons">
            <button
              className={`mode-button ${mode === 'AUTO' ? 'selected' : ''}`}
              onClick={() => setMode('AUTO')}
            >
              AUTO
            </button>
            <button
              className={`mode-button ${mode === 'MANUAL' ? 'selected' : ''}`}
              onClick={() => setMode('MANUAL')}
            >
              MANUAL
            </button>
          </div>
        </div>

        {/* 4Ï∂ï Î°úÎ¥áÌåî Ï†úÏñ¥ - ÌÇ§Î≥¥Îìú ÏûÖÎ†• */}
        <div className="arm-box">
          <h2>ü§ñ ÌÇ§Î≥¥Îìú Ï†úÏñ¥: Î°úÎ¥á Ìåî (XYZ)</h2>
          <div className="xyz-coordinates">
            <p><strong>X:</strong> {position.x}</p>
            <p><strong>Y:</strong> {position.y}</p>
            <p><strong>Z:</strong> {position.z}</p>
          </div>
          <div className="key-guide">
            <p><strong>W/S:</strong> YÏ∂ï ‚Üë‚Üì</p>
            <p><strong>A/D:</strong> XÏ∂ï ‚Üê‚Üí</p>
            <p><strong>Q/E:</strong> ZÏ∂ï ‚Üë‚Üì</p>
          </div>
        </div>

        {/* ÏßëÍ≤å ÏÉÅÌÉú ÌëúÏãú */}
        <div className="gripper-box">
          <h2>ü¶æ ÏßëÍ≤å ÏÉÅÌÉú</h2>
          <p>{gripperState ? 'ÏßëÍ≤åÍ∞Ä Î¨ºÍ≥† ÏûàÏäµÎãàÎã§' : 'ÏßëÍ≤åÍ∞Ä ÌíÄÎ†§ ÏûàÏäµÎãàÎã§'}</p>
        </div>

        {/* Ï∞®Ï≤¥ Ï†úÏñ¥ */}
        <div className="drive-box">
          <h2>üöó Ï∞®Ï≤¥ Ï†úÏñ¥ (Î©îÏπ¥ÎÑò Ìú†)</h2>
          <div className="direction-box">
            <button
              className={`direction-btn ${directionState.up ? 'pressed' : ''}`}
            >
              ‚Üë
            </button>
            <div className="horizontal-controls">
              <button
                className={`direction-btn ${directionState.left ? 'pressed' : ''}`}
              >
                ‚Üê
              </button>
              <button
                className={`direction-btn ${directionState.down ? 'pressed' : ''}`}
              >
                ‚Üì
              </button>
              <button
                className={`direction-btn ${directionState.right ? 'pressed' : ''}`}
              >
                ‚Üí
              </button>
            </div>
          </div>
          {/* Ï∞®Ï≤¥ Ïù¥Îèô Ï§ë ÌëúÏãú */}
          {directionState.moving && <div>Ï∞®Ï≤¥ Ïù¥Îèô Ï§ë...</div>}
        </div>
      </div>
    </div>
  );
};

export default Control;
