// React Î∞è ÌõÖ import
import React, { useState, useEffect } from "react";
import "./Data.css";

export default function RogiArmDashboard() {
  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:4000";
  // ÎÇ†Ïßú Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú
  const [list, setList] = useState([]);

  // ÏÑ†ÌÉùÎêú ÎÇ†Ïßú ÏÉÅÌÉú
  const [selectedDate, setSelectedDate] = useState(null);

  // ÏÉâÍπîÎ≥Ñ ÏÉÅÏûê Í∞úÏàò ÏÉÅÌÉú
  const [boxCounts, setBoxCounts] = useState({});

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú ÎÇ†Ïßú Î¶¨Ïä§Ìä∏ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    fetch(`${API_URL}/list`)
      .then((res) => res.json())
      .then((data) => setList(data));
  }, []);

  // ÎÇ†Ïßú ÌÅ¥Î¶≠ Ïãú Ìï¥Îãπ ÎÇ†ÏßúÏùò Î∞ïÏä§ Í∞úÏàò Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const handleClick = async (date) => {
    setSelectedDate(date);

    try {
      const res = await fetch(`${API_URL}/stats/${date}`);
      const json = await res.json();
      setBoxCounts({
        red: json.red_boxes,
        green: json.green_boxes,
        blue: json.blue_boxes,
      });
    } catch (err) {
      setBoxCounts({});
    }
  };

  return (
    // ÎåÄÏãúÎ≥¥Îìú Ï†ÑÏ≤¥ Ïª®ÌÖåÏù¥ÎÑà
    <div className="dashboard-container">
      <div className="dashboard-body">

        {/* ÏÇ¨Ïù¥ÎìúÎ∞î - ÎÇ†Ïßú Î™©Î°ù */}
        <aside className="dashboard-sidebar">
          <h4>üìÖ ÎÇ†Ïßú</h4>
          {list.map((item) => {
            // dummy- Ï†ëÎëêÏñ¥ Ï†úÍ±∞
            const displayTime = item.time.startsWith("dummy-")
              ? item.time.replace("dummy-", "")
              : item.time;

            return (
              <div
                key={item.file}
                className="date-entry"
                onClick={() => handleClick(item.file)}
              >
                {displayTime}
              </div>
            );
          })}
        </aside>

        {/* Î©îÏù∏ ÏòÅÏó≠ */}
        <main className="dashboard-main">

          {/* ÎèôÏòÅÏÉÅ ÏòÅÏó≠ */}
          <section className="panel large">
            <h4>üé• Ïö∞ÎÖ∏ ÎèôÏòÅÏÉÅ</h4>
            {selectedDate ? (
              <video
                src={`${API_URL}/videos/${selectedDate}.mp4`}
                controls
                width="100%"
              />
            ) : (
              <div className="video">ÎÇ†ÏßúÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.</div>
            )}
          </section>

          {/* ÌïòÎã® Ï†ïÎ≥¥ ÏòÅÏó≠ */}
          <div className="dashboard-subpanels">

            {/* ÏÉÅÏûê Í∞úÏàò ÌëúÏãú */}
            <section className="panel small">
              <h4>üì¶ ÏÉâÍπîÎ≥Ñ ÏÉÅÏûê Í∞úÏàò</h4>
              <ul>
                {Object.entries(boxCounts).map(([color, count]) => (
                  <li key={color}>
                    {color}: {count}Í∞ú
                  </li>
                ))}
              </ul>
            </section>

            {/* Ìñ•ÌõÑ Ï∂îÍ∞ÄÏö© Ìå®ÎÑê */}
            <section className="panel small">
              {/* TODO: Îã§Î•∏ Ï†ïÎ≥¥ ÌëúÏãú ÏòÅÏó≠ */}
            </section>
          </div>
        </main>
      </div>
    </div>
  );
}
